<html><style type="text/css">.giris_yap input{	 padding:20px;	margin:5px;padding:5px;width:220px; border:2px solid #ccc;border-radius:10px ;	 input[type=text]:focus {border-color:#333 };	margin-left:70px;margin-top:5px; padding:10px;background:#ccc;border:0 none;border-radius:5px;cursor:pointer;	input[type=password]:focus {border-color:#333;}	}</style><body>   <form  method="get" action="blog">				<div class="giris_yap">				        						<fieldset>							<label>FİRMA ADI</label>							<input type="text" name="firmaAd"/>						</fieldset>						<fieldset>							<label>TELEFON</label>							<input type="text" name="tel"/>						</fieldset>						<fieldset>							<label>FAX</label>							<input type="text" name="fax"/>						</fieldset>						<fieldset>							<label>ADRES</label>							<input type="password" name="adres"/>						</fieldset>																</div>			<footer>				<div>				  <input type="submit" value="Firma kaydet!" >				  				</div>			</footer>			</form></body></html><html><body>   <form name="form1" method="get" action="blog">				<div >				        						<fieldset>							<label>FİRMA ADI</label>							<input type="text" name="firmaAd"/>						</fieldset>						<fieldset>							<label>TELEFON</label>							<input type="text" name="tel"/>						</fieldset>						<fieldset>							<label>FAX</label>							<input type="text" name="fax"/>						</fieldset>						<fieldset>							<label>ADRES</label>							<input type="text" name="adres"/>						</fieldset>																</div>			<footer>				<div>				  <input type="submit" value="Firma kaydet!" >				  				</div>			</footer>			</form></body></html>